package com.janbask.dataTypes;

public class DataTypes_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a =10;
		
		int b=30;
		
		String name="Kevin";
		
		
		//System.out.println(a+b+name);
		
		System.out.println(name+a+b);
		
		System.out.println(+a+b+name);
		
		System.out.println(a+(b+name));
		
		System.out.println(name+(a+b));
	}

}
