package com.janbask.dataTypes;

public class DataTypes_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name="John";
		int age =60;
		double salary=100.976;
		
		
		// John with age 60 earns 100.976
		//John is 60 years and he earn 100.976
		
		System.out.println(name+" with age "+age+" earns "+salary); 
		
		
		System.out.println(name+" is "+age+ " years and he earns "+salary);
		
		

	}

}
