package com.janbask.relationalOperators;

public class Relational_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int k = 20;
		int l = 15;
		int p = 20;
		System.out.println(k ==l);// check whether k is equal to p , k=p means k value is p

		
		  System.out.println(k>l); //check if k is greater than l
		  System.out.println(k<l); // check if k is less than l
		  System.out.println(k>=p);// check if k is greater than or equal to l
		//System.out.println(k>=p); 
		  System.out.println(k<=l); // check if k is less than or equal to l

		  System.out.println(k<=p);
		  System.out.println(k!=l); //k is not equal to l?
		  System.out.println(k!=p); //k is not equal to p?
		  
		  
		 
	}

}
